class Solution:
    def maxValue(self, nums):
        n = len(nums)
        indexed = [(val, i) for i, val in enumerate(nums)]
        indexed.sort(key=lambda x: -x[0])
        best = -10**9
        for c in range(n):
            pair_sum = None
            cnt = 0
            s = 0
            for val, idx in indexed:
                if idx == c:
                    continue
                s += val
                cnt += 1
                if cnt == 2:
                    pair_sum = s
                    break
            if pair_sum is not None:
                cand = pair_sum - nums[c]
                if cand > best:
                    best = cand
        return best

